import React, { useEffect, useState } from "react";
import "./ShipmentInformationBlock.scss";
import { useSelector } from "react-redux";
import ShipmentCargoBayCounter from "./ShipmentCargoBayCounter";

// const initialCargoBaysRequired = () => {
//   const companyBoxesInitialAmount = useSelector((state) => state.boxes);
//   console.log(companyBoxesInitialAmount)
// }

const ShipmentInformationBlock = () => {
  let [cargoBaysRequired, setCargoBaysRequired] = useState(0);

  const companyName = useSelector((state) => state.name);
  const companyEmail = useSelector((state) => state.email);
  const companyBoxes = useSelector((state) => state.boxes);

  console.log(companyName, companyEmail, companyBoxes);

  let cargoBaysRequiredAsStringsArr = companyBoxes.split(/[,]/);
  let cargoBaysRequiredAsFloatsArr =
    cargoBaysRequiredAsStringsArr.map(parseFloat);
  let userInput = companyBoxes;
  // console.log("TOP USER INPUT: ", userInput);

  useEffect(() => {
    console.log("useEffect: ", companyName, companyBoxes);


    // setCargoBaysRequired(0)
  }, [companyName]);



  const convertNumbersToCargoBays = (input) => {
    const splitCurrentArray = input.split(/[,]/);
    const newArray = splitCurrentArray.map(parseFloat);
    const sumArray = (arr) => arr.reduce((a, b) => a + Number(b), 0);
    const roundedNumber = Math.ceil(sumArray(newArray, 1));
    return Math.ceil(roundedNumber / 10, 1);
  };

  const calculateInitialCargoBays = (boxes) => {
    cargoBaysRequired = convertNumbersToCargoBays(companyBoxes);
    setCargoBaysRequired(cargoBaysRequired)
  }

  const calculateCargoBays = (event) => {
    userInput = event.target.value;

    console.log("userInput: ", userInput);

    if (isNaN(convertNumbersToCargoBays(userInput))) {
      console.log("ERROR");
    } else {
      cargoBaysRequired = convertNumbersToCargoBays(userInput);
      console.log("requred: ", cargoBaysRequired);
      setCargoBaysRequired(cargoBaysRequired);
    }
  };

  return (
    <div className="shipmentinfo-container">
      <h1>{companyName}</h1>
      <h4>{companyEmail}</h4>
      <h3>CARGO BOXES</h3>
      <input
        onChange={calculateCargoBays}
        type="text"
        defaultValue={companyBoxes}
        // value={companyBoxes}
      />
      <h2>Number of required cargo bays</h2>
      <label><ShipmentCargoBayCounter /></label>
    </div>
  );
};

export default ShipmentInformationBlock;



