import React from "react";
import "./ShipmentInformationBlock.scss";
import { useSelector } from "react-redux";

const ShipmentInformationBlock = () => {
  const companyName = useSelector(state => state.name)
  const companyEmail = useSelector(state => state.email)
  const companyBoxes = useSelector(state => state.boxes)

  let cargoBaysRequiredAsStringsArr = companyBoxes.split(/[,]/);
  let cargoBaysRequiredAsFloatsArr =  cargoBaysRequiredAsStringsArr.map(parseFloat);

  const convertNumbersToCargoBays = () => {
    // const cargoBaysRequiredAsStringsArr = companyBoxes.split(/[,]/);
    // const cargoBaysRequiredAsFloatsArr =  cargoBaysRequiredAsStringsArr.map(parseFloat);
    console.log(cargoBaysRequiredAsFloatsArr)
  
    const sumArray = arr =>  arr.reduce((a, b) => a + Number(b), 0);
  
    console.log(sumArray(cargoBaysRequiredAsFloatsArr));
    const roundedNumber = Math.ceil(sumArray(cargoBaysRequiredAsFloatsArr, 1))
    console.log(Math.ceil(roundedNumber/10, 1))
    return Math.ceil(roundedNumber/10, 1)
  }


  const calculateCargoBays = (event) => {
    let userInput = event.target.value
    // console.log(event.target.value)
    let previousCharacter = userInput.charAt(userInput.length - 2);

    let lastCharacter = userInput.charAt(userInput.length - 1);
    console.log('-----', previousCharacter, lastCharacter)

    console.log(
      userInput.replace(userInput, lastCharacter)
    )

    let validation = (/^[,]{1,1}[0-9]{1,3}([.][0-9]{1,1})?$/).test

    if (lastCharacter === ','&& previousCharacter === ',') {
      console.log('thats all')
      userInput = userInput.length - 1
      // cargoBaysRequiredAsFloatsArr = (event.target.value)
    }


    console.log('result : ' , convertNumbersToCargoBays(event.target.value))

  }

  return (
    <div className="shipmentinfo-container">
      <h1>{companyName}</h1>
      <h4>{companyEmail}</h4>
      <h3>CARGO BOXES</h3>
      <input onChange={calculateCargoBays} type="text" defaultValue={companyBoxes}/>
      <h2>Number of required cargo bays</h2>
      <label>5</label>
    </div>
  );
};

export default ShipmentInformationBlock;
