import React, { useEffect } from "react";
import "./ShipmentInformationBlock.scss";
import store from "../../../reducers/ShipmentReducer";
import { shallowEqual, useSelector, useDispatch } from "react-redux";

const ShipmentInformationBlock = () => {
  const [companyId, companyName, companyEmail, companyBoxes] = useSelector(
    (state) => [state.id, state.name, state.email, state.boxes],
    shallowEqual
  );
  // const dispatch = useDispatch(store);
  // dispatch({ type: 'GET_SHIPMENT_INFO'});
  // const name = useSelector((state) => state.name);

  console.log("Name of the company chosen: ", companyId);

  return (
    <div className="shipmentinfo-container">
      {/* <h1>{currentShipment.name}</h1> */}
      {/* {name} */}
      {companyName}
      {companyEmail}
      {companyBoxes}

      {/* <Outlet/> */}
    </div>
  );
};

export default ShipmentInformationBlock;
