import React from "react";
import "./ShipmentListItem.scss";
import { NavLink, useLocation } from "react-router-dom";

import { useReducer } from "react";
import {
  initialState
} from "../../../../../reducers/ShipmentReducer";
import ShipmentReducer from "../../../../../reducers/ShipmentReducer";

const ShipmentListItem = (props) => {
  const location = useLocation();
  const state = props;

  const [shipment, dispatch] = useReducer(ShipmentReducer, state);


  const changeShipmentInfo = (props) => {
    dispatch({ type: "SWITCH_SHIPMENT_INFO", name: state.name });
    console.log('mmm ', shipment)
  };

  return (
    // <div className="item-container">
    <NavLink className="item-container" to={props.url} onClick={changeShipmentInfo}>
      {props.name}
    </NavLink>
    // </div>
  );
};

export default ShipmentListItem;
