import React from "react";
import "./SearchBar.scss";
import shipments from "../../../assets/data/shipments.json";
import SearchBarItems from "./SearchBarItems/SearchBarItems";

const shipmentsArray = Object.values(shipments);

const SearchBar = () => {
  const getCompanyNames = () => {
    let companyNamesArray = [];

    for (let o in shipmentsArray) {
      companyNamesArray.push(shipmentsArray[o]);
    }

    let newObj = companyNamesArray[0].map((item) => item.name);
    let newArr = Object.values(newObj);
    return newArr;
  };

  const handleTyping = (event) => {};

  // const compareEntry = (event) => {
  //   try {
  //     const companyNames = getCompanyNames();
  //     let userInput = event.target.value;
  //     console.log(userInput);

  //     let matches = [];

  //     for (let i = 0; i < companyNames.length; i++) {
  //       if (userInput.indexOf(companyNames[i][i]) != -1) {
  //         console.log(companyNames[i]);
  //         matches.push(companyNames[i])
  //         console.log(matches);

  //       }
  //     }
  //     return "no match";
  //   } catch (error) {
  //     console.log(error);
  //   }
  // };

  const compareEntry = (event) => {
    try {
      const companyNames = getCompanyNames();
      let userInput = event.target.value;
      console.log(userInput);

      let matches = [];

      if (userInput) {
        matches = companyNames.filter((data) => {
          console.log(
            data.toLocaleLowerCase().startsWith(userInput.toLocaleLowerCase())
          );
          console.log(matches)
            // matches = matches.map((data))

        });
      }

      return "no match";
    } catch (error) {
      console.log(error);
    }
  };

  compareEntry();

  // console.log(getCompanyNames());

  return (
    <div>
      <form className="form-search">
        <input
          className="input-search"
          id="search-input"
          onChange={compareEntry}
          type="search"
          placeholder="Search"
        />

        {shipmentsArray[0].map((el) => (
          <SearchBarItems key={el.name} url={el.id} name={el.name} />
        ))}
      </form>
    </div>
  );
};

export default SearchBar;
